import React, { Component } from 'react';
import Form from 'react-jsonschema-form';
import { connect } from 'react-redux';

class RUColumn extends Component {
  log = type => console.log.bind(console, type);

  getData() {
    var dataFromGet = this.props.dispatch('GET');
    console.log(`dataFromGet : ${dataFromGet}`);
    return dataFromGet ? (
      <Form
        id="read-update"
        schema={this.props.schema}
        onSubmit={this.props.dispatch('UPDATE')}
        onError={this.log('errors')}
      />
    ) : (
      <Form
        id="read-update"
        schema={this.props.schema}
        formData={dataFromGet}
        onSubmit={this.props.dispatch('UPDATE')}
        onError={this.log('errors')}
      />
    );
  }

  render() {
    return <div>{this.getData()}</div>;
  }
}

export default connect()(RUColumn);
