const tmpDataStore = (state = [], action) => {
  console.log(state, action);
  switch (action.type) {
    case 'GET':
      console.log(`data loaded! state: ${state}`);
      return state;
    case 'UPDATE':
      if (state === undefined) {
        state = {};
      }
      console.log(`previous data: ${state.data}`);
      console.log(`data updated! with data:`);
      console.log(action.data);
      state.data = action.data;
      return state;
    default:
      return state;
  }
};
export default tmpDataStore;
